import React, { useState, useEffect } from 'react';
import './eventsCreation.css';

interface Event {
  id: string;
  title: string;
  start_time: string;
  end_time: string;
}

const EventsCreation: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]); // Define the type for events
  const [eventData, setEventData] = useState({
    title: '',
    start_time: '',
    end_time: '',
  });
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');
  const [editingEventId, setEditingEventId] = useState('');

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = () => {
    fetch('http://localhost:8080/events')
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const formatTime = (dateTimeString: string) => {
    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const createEvent = () => {
    fetch('http://localhost:8080/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event created successfully');
        setModalOpen(true);
        fetchEvents();
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  const updateEvent = () => {
    // Implement the update event functionality here
    // You should send a PUT request to your Flask backend
    // with the updated eventData and the event's ID (editingEventId)
    // After a successful update, update the event details in the frontend.
    // You can clear the editingEventId state to exit edit mode.
  };

  const editEvent = (eventId: string) => {
    // When the "Edit" button is clicked, set the editingEventId state
    // to the event's ID and populate the update form with the event's details.
    const eventToEdit = events.find((event) => event.id === eventId);
    if (eventToEdit) {
      setEditingEventId(eventId);
      setEventData(eventToEdit);
    }
  };

  return (
    <div className='container'>
      <div className='left-pane'>
        <h1>Event Manager</h1>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            placeholder='Event Title'
            value={eventData.title}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Start Time:</label>
          <input
            type="datetime-local"
            name="start_time"
            value={eventData.start_time}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>End Time:</label>
          <input
            type="datetime-local"
            name="end_time"
            value={eventData.end_time}
            onChange={handleInputChange}
          />
        </div>
        {editingEventId ? (
          <button onClick={updateEvent}>Update Event</button>
        ) : (
          <button onClick={createEvent}>Create Event</button>
        )}
      </div>
      <div className='right-pane'>
        <h2>Events</h2> 
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Starts</th>
              <th>Ends</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event: any) => (
              <tr key={event.id}>
                <td>{event.id === editingEventId ? (
                  <input
                    type="text"
                    name="title"
                    value={eventData.title}
                    onChange={handleInputChange}
                  />
                ) : (
                  event.title
                )}</td>
                <td>{formatTime(event.start_time)}</td>
                <td>{formatTime(event.end_time)}</td>
                <td>
                  {event.id === editingEventId ? (
                    <button onClick={updateEvent}>Save</button>
                  ) : (
                    <button onClick={() => editEvent(event.id)}>Edit</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setModalOpen(false)}>
              &times;
            </span>
            <p>{modalMessage}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventsCreation;
