import React, { useState, useEffect } from 'react';
import './eventsCreation.css';

const EventsCreation: React.FC = () => {
  const [events, setEvents] = useState([]);
  const [eventData, setEventData] = useState({
    id: '', // Add an id field to track the edited event
    title: '',
    start_time: '',
    end_time: '',
  });
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = () => {
    fetch('http://localhost:8080/events')
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const formatTime = (dateTimeString: string) => {
    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const createEvent = () => {
    fetch('http://localhost:8080/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event created successfully');
        setModalOpen(true);
        fetchEvents();
        // Clear the input fields after creating an event
        setEventData({
          id: '',
          title: '',
          start_time: '',
          end_time: '',
        });
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  // Add a function to update an event
  const updateEvent = () => {
    fetch(`http://localhost:8080/events/${eventData.id}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event updated successfully');
        setModalOpen(true);
        fetchEvents();
        // Clear the input fields after updating an event
        setEventData({
          id: '',
          title: '',
          start_time: '',
          end_time: '',
        });
      })
      .catch((error) => console.error('Error updating event:', error));
  };

  // Handle editing an event
  const editEvent = (event: any) => {
    setEventData(event); // Set the event data to be edited
  };

  return (
    <div className='container'>
      <div className='left-pane'>
        <h1>Event Manager</h1>
        <div>
          <label>Title:</label>
          <input
            type="text"
            name="title"
            placeholder='Event Title'
            value={eventData.title}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Start Time:</label>
          <input
            type="datetime-local"
            name="start_time"
            value={eventData.start_time}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>End Time:</label>
          <input
            type="datetime-local"
            name="end_time"
            value={eventData.end_time}
            onChange={handleInputChange}
          />
        </div>
        {eventData.id ? (
          <button onClick={updateEvent}>Update Event</button>
        ) : (
          <button onClick={createEvent}>Create Event</button>
        )}
      </div>
      <div className='right-pane'>
        <h2>Events</h2>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Starts</th>
              <th>Ends</th>
              <th>Action</th> {/* Add a column for editing */}
            </tr>
          </thead>
          <tbody>
            {events.map((event: any) => (
              <tr key={event.id}>
                <td>{event.title}</td>
                <td>{formatTime(event.start_time)}</td>
                <td>{formatTime(event.end_time)}</td>
                <td>
                  <button onClick={() => editEvent(event)}>Edit</button> {/* Add an edit button */}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setModalOpen(false)}>
              &times;
            </span>
            <p>{modalMessage}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventsCreation;
