import React, { useState, useEffect } from 'react';

const apiUrl = 'http://localhost:8080';

const EventsCreation: React.FC = () => {
  const [events, setEvents] = useState([]);
  const [eventData, setEventData] = useState({
    title: '',
    start_time: '',
    end_time: '',
  });
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = () => {
    fetch('http://localhost:8080/events')
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const createEvent = () => {
    // Convert date and time values to ISO strings
    const isoStartTime = new Date(eventData.start_time).toISOString();
    const isoEndTime = new Date(eventData.end_time).toISOString();

    const updatedEventData = {
      ...eventData,
      start_time: isoStartTime,
      end_time: isoEndTime,
    };

    fetch('http://localhost:8080/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updatedEventData),
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event created successfully');
        setModalOpen(true);
        fetchEvents();
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  return (
    <div>
      <h1>Event Manager</h1>
      <div>
        <label>Title:</label>
        <input
          type="text"
          name="title"
          value={eventData.title}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>Start Time:</label>
        <input
          type="datetime-local"
          name="start_time"
          value={eventData.start_time}
          onChange={handleInputChange}
        />
      </div>
      <div>
        <label>End Time:</label>
        <input
          type="datetime-local"
          name="end_time"
          value={eventData.end_time}
          onChange={handleInputChange}
        />
      </div>
      <button onClick={createEvent}>Create Event</button>
      <div>
        <h2>Events</h2>
        <ul>  
          {events.map((event: any) => (
            <li key={event.id}>{event.title}</li>
          ))}
        </ul>
      </div>
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setModalOpen(false)}>
              &times;
            </span>
            <p>{modalMessage}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventsCreation;
