import React, { useState, useEffect } from 'react';

const apiUrl = 'http://localhost:8080';

const EventsCreation: React.FC = () => {
  const [events, setEvents] = useState([]);
  const [eventData, setEventData] = useState({
    title: '',
    start_time: '',
    end_time: '',
  });
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = () => {
    fetch('http://localhost:8080/events')
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const formatTime = (dateTimeString: string) => {
    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const createEvent = () => {
    fetch('http://localhost:8080/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event created successfully');
        setModalOpen(true);
        fetchEvents();
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const modalOverlayStyles = {
    display: modalOpen ? 'block' : 'none',
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    background: 'rgba(0, 0, 0, 0.6)', /* Semi-transparent black background */
    backdropFilter: 'blur(4px)', /* Adds a blur effect to the background */
    zIndex: 1000, /* Ensures the modal is on top of other content */
  };

  const modalBoxStyles = {
    position: 'fixed',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    background: 'white',
    padding: '20px',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    zIndex: 1001, /* Ensure modal box is on top of modal overlay */
  };

  return (
    <div>
      <h1>Event Manager</h1>
      {/* ... Rest of your code ... */}
      <div style={modalOverlayStyles} onClick={closeModal}>
        <div style={modalBoxStyles}>
          <span className="close" onClick={closeModal}>
            &times;
          </span>
          <p>{modalMessage}</p>
        </div>
      </div>
    </div>
  );
};

export default EventsCreation;
