import React, { useState, useEffect } from 'react';
import './eventsCreation.css';

interface Event {
  id: string;
  title: string;
  start_time: string;
  end_time: string;
}

const EventsCreation: React.FC = () => {
  const [events, setEvents] = useState<Event[]>([]);
  const [eventData, setEventData] = useState<Event>({
    id: '',
    title: '',
    start_time: '',
    end_time: '',
  });
  const [modalOpen, setModalOpen] = useState(false);
  const [modalMessage, setModalMessage] = useState('');

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = () => {
    fetch('http://localhost:8080/events')
      .then((response) => response.json())
      .then((data) => setEvents(data))
      .catch((error) => console.error('Error fetching events:', error));
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const formatTime = (dateTimeString: string) => {
    const dateTime = new Date(dateTimeString);
    return dateTime.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  const createEvent = () => {
    fetch('http://localhost:8080/events', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(eventData),
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event created successfully');
        setModalOpen(true);
        fetchEvents();
      })
      .catch((error) => console.error('Error creating event:', error));
  };

  const deleteEvent = (event_id: string) => {
    fetch(`http://localhost:8080/events/${event_id}`, {
      method: 'DELETE',
    })
      .then((response) => response.json())
      .then((data) => {
        setModalMessage(data.message || data.error || 'Event deleted successfully');
        setModalOpen(true);
        fetchEvents();
      })
      .catch((error) => console.error('Error deleting event:', error));
  };

  return (
    <div className='container'>
      <div className='left-pane'>
        {/* ... (Previous code remains the same) */}
      </div>
      <div className='right-pane'>
        <h2>Events</h2>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Starts</th>
              <th>Ends</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event: Event) => (
              <tr key={event.id}>
                <td>{event.title}</td>
                <td>{formatTime(event.start_time)}</td>
                <td>{formatTime(event.end_time)}</td>
                <td>
                  <button onClick={() => deleteEvent(event.id)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      {modalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={() => setModalOpen(false)}>
              &times;
            </span>
            <p>{modalMessage}</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default EventsCreation;
